    #returns 1/(1+e^-x)
    #mapping the value between 0 and 1
    def sigmoid(self, x):
        return 1 / (1 + np.exp(-x))

    def sigmoid_deriv(self, x):
        return x * (1 - x)

    #randomly generate initial synapses for a layer with pre_syn presynaptic neurons and post_syn post synaptic neurons
    def generate_synapses(self, pre_syn, post_syn):
        return np.random.random((pre_syn, post_syn)) * 2 - 1

    def build_network(self):
        self.syn.append(self.generate_synapses(mv.spl_cols, mv.nodes_per_hidden_layer))
        [self.syn.append(self.generate_synapses(mv.nodes_per_hidden_layer, mv.nodes_per_hidden_layer)) for i in range(mv.hidden_layers)]
        self.syn.append(self.generate_synapses(mv.nodes_per_hidden_layer, mv.ip_size))

    #run network with inputs _pool and return layer _out
    def feed_forward(self, _pool, _out):
        self.lay[0] = _pool
        for i in range(len(self.lay)-1):
            self.lay[i+1] = self.sigmoid(np.dot(self.lay[i], self.syn[i]))
        if _out != None:
            return self.lay[_out]

    def back_prop(self, _epochs):
        for i in range(_epochs):
            j = (i / _epochs) * 100
            if j % 10==0:
                print("Training " + str(j) + "%" + " complete")
            self.feed_forward(self.inputs, None)
            for j in range(len(self.lay)-1, -1, -1):
                if j == len(self.lay)-1:
                    self.lay_err[j] = self.outputs - self.lay[j]
                else:
                    self.lay_err[j] = self.lay_delta[j+1].dot(self.syn[j].T)
                self.lay_delta[j] = self.lay_err[j] * self.sigmoid_deriv(self.lay[j])
                if j != len(self.lay)-1:
                    self.syn[j] =+ self.lay[j].T.dot(self.lay_delta[j+1])
